raw_commands:
{% set pricing = get_erpnext_price(doc.item_code) %}

SIZE 2,1
DIRECTION 1
CLS

TEXT 128,4,"1",0,1,1,"CLOTHING CENTER"

{% if doc.item_code|length >= 17 %}
BARCODE 56,20,"128",40,2,0,1,2,"{{doc.item_code}}"
{% elif doc.item_code|length >= 10 %}
BARCODE 15,20,"128",40,1,0,2,2,"{{doc.item_code}}"
{% else %}
BARCODE 120,20,"128",40,2,0,2,2,"{{doc.item_code}}"
{% endif %}

BAR 0,80,406,5

{% if pricing.discount_percent %}

TEXT 55,100,"2",0,1,1,"PRECIO:"
TEXT 35,132,"3",0,1,1,"{{ pricing.formatted_mrp }}"

BAR 198,85,5,204

TEXT 258,100,"2",0,1,1,"OFERTA:"
TEXT 238,132,"3",0,1,1,"{{ pricing.formatted_price }}"
TEXT 280,170,"2",0,1,1,"{{ pricing.formatted_discount_percent }}"

{% else %}

TEXT 145,108,"2",0,1,1,"PRECIO:"
TEXT 125,148,"3",0,1,1,"{{ pricing.formatted_price }}"

{% endif %}

PRINT 1,1
END
